module.exports = inject

function inject(bot, options) {
    bot.activatePotions = (Potion) => {
        // console.log(Potion);
        const items = bot.inventory.items();
        const potionFound = items.some(item => {
            if (item.nbt && item.nbt.value && item.nbt.value.Potion && item.nbt.value.Potion.value === Potion) {
                bot.equip(item, 'hand', () => { });
                bot.activateItem();
                return true; // Зелье найдено, заканчиваем выполнение функции
            }
        });

        if (!potionFound) {
            // console.log('Potion not found in inventory'); // Вывод сообщения на английском, если зелье не найдено
        }
    };
}